import React , {useState} from 'react';


function App(){
    const [result, updateResult] = useState(0);
    let toBeOperated = "";
    function handleClick(event){
        if(event.target.value === "reset"){
            updateResult(0);
            toBeOperated = "";
        }
        else if(event.target.value === "="){
            updateResult(0);
        }
        console.log(event.target.value);
    }

    return <div className='calculator'>
        <div className='result'>{result}</div>
        <div className='buttons'>
            <button onClick={handleClick} className = "cbutton function"  value = "reset">AC</button>
            <button onClick={handleClick} className = "cbutton function"  value = "=">±</button>
            <button onClick={handleClick} className = "cbutton function"  value = "/100">%</button>
            <button onClick={handleClick} className = "cbutton operator" value = "/">÷</button>
            <button onClick={handleClick} className = "cbutton" value = "7">7</button>
            <button onClick={handleClick} className = "cbutton" value = "8">8</button>
            <button onClick={handleClick} className = "cbutton" value = "9">9</button>
            <button onClick={handleClick} className = "cbutton operator" value = "=">×</button>
            <button onClick={handleClick} className = "cbutton" value = "4">4</button>
            <button onClick={handleClick} className = "cbutton" value = "5">5</button>
            <button onClick={handleClick} className = "cbutton" value = "6">6</button>
            <button onClick={handleClick} className = "cbutton operator" value = "-">-</button>
            <button onClick={handleClick} className = "cbutton" value = "1">1</button>
            <button onClick={handleClick} className = "cbutton" value = "2">2</button>
            <button onClick={handleClick} className = "cbutton" value = "3">3</button>
            <button onClick={handleClick} className = "cbutton operator" value = "+">+</button>
            <button onClick={handleClick} className = "cbutton number-0" value = "0">0</button>
            <button onClick={handleClick} className = "cbutton" value = ".">.</button>
            <div onClick={this.handleClick} className = "cbutton operator" value = "=">=</div>
        </div>
    </div>;
}

export default App;
